INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (402013, 'Mike Smith', '1972-08-27', null, 'Mike Smith is a Canadian actor and musician born in New Glasgow, Nova Scotia, and raised in Thorburn. He earned a degree in English from St. Francis Xavier University in Antigonish, Nova Scotia. He played hockey for the Pictou County Weeks of the Nova Scotia Midget AAA Hockey League when he was in high school. For a short time in 2006, he was engaged to actress Nichole Hiltz. He is best known for his portrayal of "Bubbles" and co-writer of the ongoing cult classic television program, films and stage production Trailer Park Boys.');
