INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (416698, 'Richard Jenkins', '1947-05-04', null, 'Richard Dale Jenkins (born May 4, 1947) is an American stage, film, and television actor. After beginning his career in theatre, Jenkins made his film debut in 1974, and appeared in supporting roles in numerous film productions in the 1980s and the 1990s before coming to prominence in the early 2000s for playing the deceased patriarch Nathaniel Fisher on the HBO funeral drama series Six Feet Under. He has since established himself in Hollywood by appearing in lead roles in the films The Visitor and The Cabin in the Woods.
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (419074, 'Zoe Kazan', '1983-09-09', null, 'Zoe Kazan is an American stage, film and television actress and writer, best known for playing lead characters in the feature films "Ruby Sparks", for which she also wrote the screenplay, "What If", "In Your Eyes", and "The Big Sick". She holds a BA in Theatre from Yale University, New Haven, Connecticut, USA.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (411482, 'Bill Murray', '1950-09-21', null, 'William James "Bill" Murray (born September 21, 1950) is an American actor and comedian. He first gained national exposure on Saturday Night Live and later went on to star in a number of critically and commercially successful comedic films, including Caddyshack (1980), Ghostbusters (1984), and Groundhog Day (1993). Murray gained additional critical acclaim later in his career, starring in Lost in Translation (2003), for which he was nominated for an Academy Award, and a series of films directed by Wes Anderson, including Rushmore (1998), The Royal Tenenbaums (2001), The Life Aquatic with Steve Zissou (2004) and Fantastic Mr. Fox (2009).
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (446917, 'Martha Wainwright', '1976-05-08', null, 'Martha Wainwright (born May 8, 1976) is a Canadian-American folk-rock singer-songwriter. She is the daughter of American folk singer and actor Loudon Wainwright III and Canadian folk singer-songwriter Kate McGarrigle. She was raised in a musical family along with her older brother, Rufus Wainwright, in Montreal, Quebec, Canada.');
