INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (420752, 'Aidan Gillen', '1968-04-24', null, 'Aidan Gillen (born Aidan Murphy on 24 April 1968) is an Irish stage and screen actor, best known in the UK for his role as Stuart Jones in the ground-breaking Channel 4 television series Queer as Folk. He is perhaps best known to US audiences for his role in HBO''s television series The Wire in which he plays Tommy Carcetti and for his role as Petyr Baelish in the HBO series Game of Thrones. Description above from the Wikipedia article Aidan Gillen, licensed under CC-BY-SA, full list of contributors on Wikipedia.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (421890, 'Gwendoline Christie', '1978-10-28', null, 'Gwendoline Christie (born 28 October 1978) is an English actress and model. She is known for portraying the warrior Brienne of Tarth in the HBO fantasy-drama series Game of Thrones. In film, Christie started with minor roles in the Terry Gilliam films The Imaginarium of Doctor Parnassus and The Zero Theorem, and played Captain Phasma in Star Wars: The Force Awakens.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (436592, 'Isaac Hempstead-Wright', '1999-04-09', null, 'Isaac Hempstead Wright is known for his role as Bran Stark in the HBO series Game of Thrones, and also for his role as Tom in The Awakening. Isaac Hempstead Wright began acting aged 11.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (448107, 'Kristofer Hivju', '1978-12-07', null, 'Kristofer Hivju is a Norwegian actor, producer and screenwriter who has experience in theater, television, radio and film. He is the son of actor Erik Hivju. Hivju has had roles in television series such as Fox Grønland and Seks som oss, the movie Rovdyr and written scripts for short films Closework in 2005 and Flax from 2008. He completed in 2004 a four-year degree in theater arts at the Russian State Academy of Theatre Art (GITIS) in Denmark.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (458178, 'Nathalie Emmanuel', '1989-04-02', null, 'Nathalie Joanne Emmanuel (born 2 March 1989) is an English actress best known for her role as Sasha Valentine in the soap opera Hollyoaks, Computer Hacktivist Ramsey in the action film Furious 7 and as the interpreter Missandei on the HBO fantasy series Game of Thrones.');
