INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (415688, 'Johnny Galecki', '1975-04-30', null, 'Johnny Galecki is a Belgian-born American actor, best known for his roles as David Healy in Roseanne, Rusty Griswold in National Lampoon''s Christmas Vacation, and as Leonard Hofstadter in The Big Bang Theory.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (5384, 'Jim Parsons', '1973-03-24', null, 'Jim Parsons is an American television and film actor, best known for playing Sheldon Cooper in The Big Bang Theory.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (429930, 'Kaley Cuoco', '1985-11-30', null, 'Kaley Cuoco is an American film and television actress and singer. She is best known for her roles as Bridget Hennessy on the sitcom 8 Simple Rules, Billie Jenkins on the supernatural drama Charmed, and Penny on the sitcom The Big Bang Theory.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (421178, 'Simon Helberg', '1980-12-09', null, 'Simon Helberg is an American actor and comedian, best known for his role as Howard Wolowitz in The Big Bang Theory.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (466846, 'Kunal Nayyar', '1981-04-30', null, 'Kunal Nayyar is an Indian actor born in London, England, and raised in New Delhi, India, best known for his role as Rajesh Koothrappali in The Big Bang Theory.');
