INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (421315, 'Patrick J. Adams', '1981-08-27', null, 'Patrick J. Adams is a Canadian stage, film and television actor and photographer, best known for his leading role as Mike Ross on the television drama "Suits". He holds a BFA in Acting from the University of Southern California, Los Angeles, USA.');
