INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (295837, 'Sarah Hyland', '1990-11-24', null, 'Sarah Jane Hyland (born November 24, 1990) is an American actress, currently starring as Haley Dunphy on the ABC comedy Modern Family.  Description above from the Wikipedia article Sarah Hyland, licensed under CC-BY-SA, full list of contributors on Wikipedia.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (420129, 'Ariel Winter', '1998-01-28', null, 'Ariel Winter Workman (born January 28, 1998), known in television shows and films as, Ariel Winter, is an American teen actress and singer. She is best known for her role as Alex Dunphy in the TV series Modern Family, for which she has won a Screen Actors Guild Award for Best Ensemble in a Comedy Series, along with the rest of the show''s cast.
