INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (424936, 'Gustaf Skarsgård', '1980-11-12', null, 'Gustaf Skarsgård (born November 12, 1980 in Stockholm) is a Swedish actor, son of My and Stellan Skarsgård and brother of Eija, Valter, Sam, Bill and Alexander Skarsgård. His godfather is acclaimed Swedish actor Peter Stormare.
