INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (432852, 'Alison Brie', '1982-12-29', null, 'Alison Brie Schermerhorn (born December 29, 1982), better known by her stage name Alison Brie, is an American actress. She is best known for portraying Annie Edison on the NBC sitcom Community and Trudy Campbell on the AMC drama Mad Men. She has also starred in films such as Scream 4 (2011) and The Five-Year Engagement (2012).
