INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (413602, 'Matthew Modine', '1959-03-22', null, 'Matthew Avery Modine (born March 22, 1959) is an award-winning American actor. His film roles include Private Joker in Stanley Kubrick''s Full Metal Jacket, the title character in Alan Parker''s Birdy, high school wrestler Louden Swain in Vision Quest, and the oversexed Sullivan Groff in Weeds.');
