INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (417130, 'Charlie Hunnam', '1980-04-10', null, 'Charles Matthew "Charlie" Hunnam (born 10 April 1980) is an English actor. He is perhaps best known to UK audiences as Pete Dunham in Green Street Hooligans and as Nathan Maloney in the Channel 4 hit drama Queer as Folk and to US audiences as Jackson "Jax" Teller in the FX network series, Sons of Anarchy.');
