INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (411738, 'Paul Wesley', '1982-07-23', null, 'Paul Wesley was born on July 23, 1982. He was born and raised in New Jersey. He has an older sister, Monika, and two younger sisters, Julia and Leah. Paul became interested in acting when enrolled in a summer arts program in third grade. His television debut was on NBC''s "Another World" (1964) where he portrayed the character of ''Sean McKinnon''.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (17093, 'Ian Somerhalder', '1978-12-08', null, 'Ian Somerhalder (born December 8, 1978) is an American model, actor and producer and founder of the Ian Somerhalder Foundation. He is best known for playing Damon Salvatore in The Vampire Diaries and Boone Carlyle in Lost.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (424259, 'Kat Graham', '1989-09-05', null, 'Katerina Alexandre Hartford "Kat" Graham (born September 5, 1989) is a Swiss-born American actress, model, singer and dancer. Her film roles include The Parent Trap (1998), 17 Again (2009), The Roommate (2011) and Honey 2 (2011). She is known for her role as Bonnie Bennett on The CW supernatural drama The Vampire Diaries.
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (421509, 'Candice King', '1987-05-13', null, 'Candice King is an American actress and singer, best known for her role as Caroline Forbes on The Vampire Diaries.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (432218, 'Zach Roerig', '1985-02-22', null, 'Zach Roerig (born February 22, 1985) is an American actor who is best known for roles of Casey Hughes on As the World Turns, Hunter Atwood on One Life to Live and Matt Donovan on The Vampire Diaries.');
