INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (428616, 'Zachary Quinto', '1977-06-02', null, 'Zachary Quinto is a american actor best known for his roles as the evil Sylar on TV''s Heroes and as Spock in the Star Trek Reboot.  In 1999 Quinto graduated from Carnegie Melon''s School of Drama, afterwards beginning his career as a television actor.  In 2003, he landed a recurring role on 24, appearing as a computer specialist working for CTU. Later, in 2006, he began working on Heroes, playing the evil serial killer Sylar.   In 2009 he played Spock in Star Trek, a role which he was lauded for bringing his own spin to a decades old character and a role he is set to reprise in the second film in the new series.   Like his costar Chris Pine, Zachary has taken some time to perform on stage, performing in various productions including Shakespeare''s Much Ado About Nothing.  Quinto is signed up to play George Gershwin in the Steven Spielberg directed Biopic about the composer. ');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (297417, 'Tatiana Maslany', '1985-09-22', null, 'Tatiana Maslany is a Canadian film and television actress, best known for her lead portrayal of multiple characters in the Canadian science fiction thriller series "Orphan Black". For her performance, she received widespread critical acclaim, winning the Primetime Emmy Award for Best Lead Actress in a Drama Series in 2016, becoming the first Canadian actress from a Canadian series to win an Emmy in a key dramatic category.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (437284, 'Jordan Gavaris', '1989-09-25', null, 'Jordan Gavaris is a Candian film and television actor and screenwriter, best known for his portrayal of Felix "Fee" Dawkins in the television series "Orphan Black".');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (470113, 'Kevin Hanchard', '1974-07-04', null, 'Kevin Hanchard is a Canadian stage and screen actor, best known for his roles as Detective Arthur "Art" Bell in the television series "Orphan Black" and as Inspector Sematimba in "The Expanse".');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (468085, 'Kristian Bruun', '1979-10-25', null, 'Kristian Bruun is a Canadian stage and screen actor, producer and writer, best known for his regular role as Donald "Donnie" Hendrix in the television series "Orphan Black". He attended the Valley Forge Military Academy in Pennsylvania, the Queen''s University in Ontario, and the George Brown Theater School in Toronto.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (366598, 'Ari Millen', '1982-01-19', null, 'Ari Millen is a Canadian stage and screen actor, best known for playing the male clones in the television series "Orphan Black". He holds a BFA in Acting from Ryerson University, Toronto, Ontario, Canada.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (418754, 'Maria Doyle Kennedy', '1964-09-25', null, 'Maria Doyle Kennedy is an Irish film and television actress, singer and songwriter, best known for her roles as Queen Katherine of Aragon in the television series "The Tudors", the title character''s wife Frances in "The General", and as Siobhan "Mrs. S" Sadler in the television series "Orphan Black".');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (903659, 'Josh Vokey', null, null, 'Josh Vokey is a Canadian stage and screen actor, best known for playing science geek Scott Smith on the television series "Orphan Black". He graduated from the Sheridan College, Oakville, and from the Theatre School at the University of Toronto, Ontario, Canada.');
