INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (297957, 'Catherine Tate', '1968-05-12', null, 'Catherine Tate (born 12 May 1968)  is an English actress, writer and comedian. She has won numerous awards for her work on the sketch comedy series The Catherine Tate Show as well as being nominated for an International Emmy Award and four BAFTA Awards. Following the success of The Catherine Tate Show, Tate played Donna Noble in the 2006 Christmas special of Doctor Who and later reprised her role, becoming the Doctor''s companion for the fourth series in 2008.
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (412600, 'Simon Baker', '1969-07-30', null, 'Simon Baker (born 30 July 1969, height 5'' 9¾" (1,77 m)) is an Australian actor and director. In his television acting career, he is best known for his lead role in the CBS television series The Mentalist as Patrick Jane and as Nicholas Fallin in The Guardian.
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (415523, 'Robin Tunney', '1972-06-19', null, 'Robin Jessica Tunney (born June 19, 1972, height 5'' 4" (1,63 m)) is an American actress. She is known for her role as Teresa Lisbon in the television series The Mentalist (2008–present), as well as previous roles in the films Encino Man (1992), Empire Records (1995), The Craft (1996), End of Days (1999), and Vertical Limit (2000), and the 2005–06 series Prison Break.
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (417706, 'Tim Kang', '1973-03-16', null, 'Tim Kang (born March 16, 1973, in San Francisco, California), is a Korean American television and film actor.');
