INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (434821, 'Anna Hutchison', '1986-02-08', null, 'Hutchison''s career began on the New Zealand soap opera Shortland Street and continued in the American series Power Rangers Jungle Fury. She also played Allison Dine, the girlfriend of crime figure Terry Clark in the Australian television series Underbelly: A Tale of Two Cities also known as Underbelly: The Mr Asia Story,[2] and Amy Smart in the New Zealand television comedy-drama series Go Girls. In 2012, she starred in the Joss Whedon-Drew Goddard meta-fictional slasher film The Cabin in the Woods.');
