INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (8721, 'Benjamin Bratt', '1963-12-16', null, 'Benjamin Bratt (born December 16, 1963) is an American actor. He is most famous for his role as Rey Curtis on the TV series Law &amp; Order; and his appearances in the movies Blood in Blood Out, Miss Congeniality, Cloudy with a Chance of Meatballs, Traffic, and Piñero. He starred on the A&amp;E Network drama The Cleaner.');
