INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (412714, 'Mitch Pileggi', '1952-04-05', null, 'Mitchell Craig "Mitch" Pileggi (born April 5, 1952) is an American actor. Pileggi is known for playing FBI assistant director Walter Skinner on the long-running popular series The X-Files. He also had a recurring role on Stargate Atlantis as Col. Steven Caldwell. He appeared in the 2008 film, Flash of Genius.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (414778, 'William B. Davis', '1938-01-13', null, 'William Bruce Davis (born January 13, 1938) is a Canadian actor, known for his role as The Smoking Man on The X-Files. He has also appeared in Stargate SG-1 as Damaris, a Prior of the Ori and as Mayor Tate on Smallville. Most recently he has appeared in FOX''s Human Target as an aging mobster.
