INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (421648, 'Terry Crews', '1968-07-30', null, 'Terrence Alan "Terry" Crews is an American actor, comedian, voice artist former player in the National Football League. He is perhaps best known for his role as family patriarch Julius on the UPN/CW sitcom Everybody Hates Chris. Currently, he stars in the TBS sitcom Are We There Yet? as Nick Kingston-Persons. Additionally, he stars in the BET reality series The Family Crews, and has appeared in such films as The 6th Day, Friday After Next, White Chicks, Idiocracy and The Expendables. In 2010, Crews was featured in several Old Spice TV commercials for their "Odor Blocker Body Wash", directed by Tim Heidecker and Eric Wareheim.
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (437944, 'Melissa Fumero', '1982-08-19', null, 'Melissa Fumero is an American actress. She is known for her roles as Adriana Cramer on One Life to Live and Zoe on Gossip Girl. Fumero stars as Amy Santiago in the Fox comedy series Brooklyn Nine-Nine.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (470500, 'Chelsea Peretti', '1978-02-20', null, 'Chelsea Vanessa Peretti (born February 20, 1978) is an American stand-up comedian, actress and writer. She is known for portraying Gina Linetti on the Fox comedy series Brooklyn Nine-Nine. Paste named her Twitter one of the "The 75 Best Twitter Accounts of 2014", ranking it at #75.');
