INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (9914, 'Steve Buscemi', '1957-12-13', null, 'Steven Vincent "Steve" Buscemi is an American actor, writer and film director. He was born in Brooklyn, New York, the son of Dorothy, who worked as a hostess at Howard Johnson''s, and John Buscemi, a sanitation worker and Korean War veteran. Buscemi''s father was Sicilian American and his mother Irish American. He has three brothers: Jon, Ken, and Michael. Buscemi was raised Roman Catholic.
