INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (298488, 'David Tennant', '1971-04-18', null, 'David Tennant (born David John McDonald on 18 April 1971) is a Scottish actor. In addition to his work in theatre, including a widely praised Hamlet, Tennant is best known for his role as the tenth incarnation of the Doctor in Doctor Who, along with the title role in the 2005 TV serial Casanova and as Barty Crouch, Jr. in the 2005 film adaptation, Harry Potter and the Goblet of Fire.');
