INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (485, 'Daryl Hannah', '1960-12-03', null, 'Daryl Hannah was born and raised in Chicago, Illinois. She attend the University of Southern California (USC) and made her film debut in the Brian De Palma film The Fury (1978). She starred in such films as Ron Howard''s Splash (1984) and ''Herbert Ross'' Steel Magnolias (1989). She won the Jury Award from the Berlin International Film Festival for Best Short in 1994 for a film titled The Last Supper (1993), which she directed, wrote, and produced.');
