INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (435374, 'Erica Tazel', null, null, 'Erica Tazel is an American stage and screen actress, best known for playing supporting character Rachel Brooks in the FX television series "Justified". She graduated with a BA from Spelman College and an MFA from New York University''s Graduate Acting Program.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (417514, 'Walton Goggins', '1971-11-10', null, 'Walton Sanders Goggins, Jr. (born November 10, 1971) is an American actor best known for his roles on the FX Networks series The Shield and Justified, portraying Detective Shane Vendrell and Boyd Crowder, respectively. He is also known for his character roles in the films Miracle at St. Anna and Predators. Goggins co-produced and starred in the 2001 short film The Accountant, which won an Academy Award for Best Live Action Short Film. Goggins accepted the award alongside Ray McKinnon and Lisa Blount.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (436770, 'Natalie Zea', '1975-03-17', null, '​Natalie Zea (born March 17, 1975 height 5'' 5" (1,65 m)) is an American actress known for her roles as Gwen Hotchkiss on the NBC daytime soap Passions, socialite Karen Darling on Dirty Sexy Money on ABC and Winona Hawkins on Justified on FX crime drama Justified (2010-2013), and as Claire Matthews in Fox series The Following (2013).
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (9276, 'Karl Urban', '1972-06-07', null, 'He is known for playing Éomer in the second and third installments of Peter Jackson''s The Lord of the Rings trilogy, Dr. Leonard "Bones" McCoy in the 2009 film Star Trek and Julius Caesar on Xena: Warrior Princess. He played Vaako in The Chronicles of Riddick, the Russian assassin Kirill in The Bourne Supremacy, and Ghost in Pathfinder and won acclaim for his performances in New Zealand films The Price of Milk and Out of the Blue.
