INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (412461, 'Julianna Margulies', '1966-06-08', null, 'Julianna Margulies is an American film and television actress, best known for her recurring role as Nurse Carol Hathaway on the NBC medical drama "ER", for which she won an Emmy Award, and her lead role of lawyer Alicia Florrick in the CBS legal drama "The Good Wife", for which she won a Golden Globe, two Emmy and two Screen Actors Guild Awards.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (422742, 'Matt Czuchry', '1977-05-20', null, 'Matt Czuchry is an American actor, best known for his portrayals of Logan Huntzberger on "Gilmore Girls" and of lawyer Cary Agos on the CBS television series "The Good Wife".');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (11113, 'Christine Baranski', '1952-05-02', null, 'Christine Baranski is an American stage and screen actress, best known for her portrayals of Maryann Thorpe on the television series "Cybill" and of lawyer Diane Lockhart in the CBS legal drama "The Good Wife".');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (298290, 'Alan Cumming', '1965-01-27', null, 'Alan Cumming, OBE, is a Scottish-American stage and screen actor, singer, comedian, director, producer and author. He is best known for playing Emcee in "Cabaret" on stage, for his portrayal of Boris Grishenko in the James Bond movie "GoldenEye" and as Nightcrawler in "X2", for his recurring role of Eli Gold in the CBS television series "The Good Wife", and for his autobiography "Not My Father''s Son: A Memoir".');
