INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (436641, 'Jesse Rath', null, null, 'Born and raised in Montreal, Jesse Rath is best known for his role as Carter Boyd in the CBC series 18 to Life, for which he received a 2010 and 2011 Gemini Award nomination. Other small screen credits include lead roles in Mudpit and Disney Channel’s Aaron Stone as well as guest star appearances in Cra$h and Burn, My Babysitter’s a Vampire, and Being Human. Rath’s feature film credits include The Greatest Game Ever Played, Prom Wars, The Howling: Reborn and The Trotsky (opposite Jay Baruchel).
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (425752, 'Nichole Galicia', '1975-03-27', null, 'Nichole Galicia is a film and television actress whose journey to the big screen began as a high fashion model on the catwalks of Europe and New York. Most recently, acclaimed director Quentin Tarantino hand picked Galicia for his film, Django Unchained, starring Leonardo DiCaprio and Jamie Foxx.');
