INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (432255, 'Annabelle Wallis', '1984-09-25', null, 'Annabelle Frances Wallis (born 5 September 1984) is an English actress best known for her roles as Jane Seymour in Showtime''s period drama The Tudors, Bridget Pierce in ABC''s drama Pan Am, Mia Form in the 2014 supernatural horror film Annabelle, Grace Burgess in the BBC drama Peaky Blinders, and Jenny Halsey in the 2017 action film The Mummy.
