INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (244084, 'Joe Cole', '1988-11-28', null, 'Joe Cole (born 28 November, 1988) is an English actor from Kingston, London. Some of his most notable roles include playing Luke in Skins, Tommy in Offender, John Shelby in Peaky Blinders, Reece in Green Room, and Marzin and Beckwith in Secret in Their Eyes.');
