INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (423126, 'Brian Austin Green', '1973-07-15', null, 'From Wikipedia, the free encyclopedia.  Brian Austin Green (born July 15, 1973) is an American actor. He is best known for his portrayal of David Silver on the television series Beverly Hills, 90210, a role he played from 1990 to 2000.  Green also starred in the sitcom Freddie and Terminator: The Sarah Connor Chronicles.  Since 2009, he has appeared in a recurring role on Smallville as Metallo. Green is married to actress and model Megan Fox.  Description above from the Wikipedia article Brian Austin Green, licensed under CC-BY-SA,full list of contributors on Wikipedia.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (426661, 'Leven Rambin', '1990-05-17', null, 'Leven Rambin was born on May 17, 1990 in Houston, Texas, USA as Leven Alice Rambin. She is an actress and director, known for The Hunger Games (2012), Percy Jackson: Sea of Monsters (2013) and Chasing Mavericks (2012). She has been married to Jim Parrack since October 10, 2015.');
