INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (425659, 'Casey Wilson', '1980-10-24', null, 'Cathryn Rose "Casey" Wilson (born October 24, 1980) is an American actress, comedienne and screenwriter. She was a cast member on Saturday Night Live from 2008–2009 and currently stars as Penny Hartz in the ABC comedy series Happy Endings.
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (416658, 'Nick Zano', '1978-03-08', null, 'Nick Zano (born March 8, 1978) is an American actor and producer. He is best known for playing Vince in The WB''s sitcom What I Like About You. He got his big break on MTV, where he hosted that network''s former infotainment program about the film industry, Movie House, and briefly worked as an MTV News correspondent before he began an acting career.');
