INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (407229, 'Jon Lajoie', '1980-08-21', null, 'Jonathan "Jon" Lajoie (born August 21, 1980) is a French-Canadian comedian, actor, rapper, singer, musician and Internet celebrity from Montreal, Quebec. He gained fame mostly from his YouTube channel, "jonlajoie," posting comedic original songs (as rapper E=MC Vagina) and comedy skits. Lajoie is also known for his role as Taco MacArthur on theFX comedy series The League.');
