INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (458172, 'Jessica De Gouw', '1988-02-15', null, 'Jessica Elise De Gouw (born 15 February 1988), sometimes credited as Jess De Gouw, is an Australian actress. She is known for her role as Helena Bertinelli/The Huntress in the television series Arrow, and as Mina Murray in the NBC TV series, Dracula.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (11308, 'Thomas Kretschmann', '1962-09-08', null, 'Thomas Kretschmann (born 8 September 1962) is a German actor best known for playing Leutnant Hans Von Witzland in the 1993 film Stalingrad, Hauptmann Wilm Hosenfeld in The Pianist, Hermann Fegelein in Der Untergang, and Captain Englehorn in the 2005 remake of King Kong.
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (424005, 'Nonso Anozie', '1979-05-28', null, 'Chukwunonso Nwachukwu "Nonso" Anozie is a British actor who has worked on stage, film, and television. He is best known for his role as Tank in RocknRolla (2008), Seargent Dap in Ender''s Game (2008), Abraham Kenyatta in the TV series Zoo (2015), and Xaro Xhoan Daxos in the HBO TV series Game of Thrones (2011).');
